<script setup lang="ts">
import {ChatMessage} from "../apis/MessageApi";
import {useUserStore} from "../store/UserStore";
import {reactive} from "vue";
import {DownloadOutlined} from "@ant-design/icons-vue";

const props = defineProps<{
  item: ChatMessage
}>()

const userStore = useUserStore()

const isReversal = reactive({
  'mirror-reversal': false
})
const fileJustify = reactive({
  'justify-content': 'flex-start'
})
if (props.item.from.id === userStore.user.id) {
  isReversal['mirror-reversal'] = true
  fileJustify['justify-content'] = 'flex-end'
}
</script>

<script lang="ts">
export default {
  name: "Bubble"
}
</script>

<template>
  <a-comment :class="isReversal">
    <template #author>{{ item.from.name }}</template>
    <template #avatar>
      <a-avatar src="https://joeschmoe.io/api/v1/random" alt="头像"/>
    </template>
    <template #content>
      <a-image
          v-if="item.type === 'IMAGE'"
          height="200px"
          :src="item.imageBase64"
          fallback="data:image/png;base64,"
      />
      <a-typography-text class="bubble-text" v-else-if="item.type === 'TEXT'" code>{{ item.text }}</a-typography-text>
      <p class="bubble-file" :style="fileJustify" v-else-if="item.type === 'FILE'">
        <a-card style="width: fit-content;background-color: rgba(237,237,237,0.88);">
          <a-card-meta :title="item.originalFileName"/>
          <template #actions>
            <span v-if="isReversal['mirror-reversal']">文件</span>
            <a :href="'http://localhost:8080/fileResource' + item.path"
               :download="item.originalFileName" target="_blank"
            >
              <download-outlined/>
            </a>
            <span v-if="!isReversal['mirror-reversal']">文件</span>
          </template>
        </a-card>
      </p>
    </template>
    <template #datetime>
      <span>{{ item.date }}</span>
    </template>
  </a-comment>
</template>

<style scoped lang="scss">
.mirror-reversal {
  :deep(.ant-comment-inner) {

    .ant-comment-avatar {
      order: 2;
      margin-right: 0;
      margin-left: 12px;
    }

    .ant-comment-content {
      text-align: right;

      .ant-comment-content-author {
        justify-content: flex-end;

        .ant-comment-content-author-name {
          order: 2;
        }
      }
    }
  }
}

.bubble-text {
  font-size: x-large;
}
.bubble-file {
  display: flex;
  align-items: center;
}
</style>