<script setup lang="ts">
import {ChatMessage} from "../apis/MessageApi";
import {useUserStore} from "../store/UserStore";
import {markRaw, reactive, ref} from "vue";
import {DownloadOutlined} from "@ant-design/icons-vue";
import FileApi from "../apis/FileApi";
import {message} from "ant-design-vue";

const props = defineProps<{
  item: ChatMessage
}>()

const userStore = useUserStore()

const isReversal = reactive({
  'mirror-reversal': false
})
const fileJustify = reactive({
  'justify-content': 'flex-start'
})
if (props.item.from.id === userStore.user.id) {
  isReversal['mirror-reversal'] = true
  fileJustify['justify-content'] = 'flex-end'
}
const downloadFile = (id: number) => {
  FileApi.downloadFile(id).then((resp) => {
    if (resp.data.code) {
      message.error(resp.data.message)
    } else {
      let blob = new Blob([resp.data], {
        type: resp.data.type,
      })
      // 创建下载标签
      let downloadElement = document.createElement("a")
      // 将blob转换为url
      let href = window.URL.createObjectURL(blob)
      downloadElement.href = href
      // 获取响应头中的文件名
      let disposition = resp.headers['content-disposition'] as string
      let filename = disposition.substring(disposition.lastIndexOf('=') + 1)
      console.log('filename', filename)
      downloadElement.download = filename
      downloadElement.target="_blank"
      // 触发点击事件下载文件
      downloadElement.click();
      window.URL.revokeObjectURL(href);
    }
  }).catch(() => {
    message.error('下载文件出错')
  })
}
</script>

<script lang="ts">
export default {
  name: "Bubble"
}
</script>

<template>
  <a-comment class="bubble" :class="isReversal">
    <template #author>{{ item.from.name }}</template>
    <template #avatar>
      <a-avatar src="https://joeschmoe.io/api/v1/random" alt="头像"/>
    </template>
    <template #content>
      <a-image
          v-if="item.type === 'IMAGE'"
          width="20vw"
          :src="item.imageBase64"
          fallback="data:image/png;base64,"
      />
      <a-typography-paragraph class="bubble-text" v-else-if="item.type === 'TEXT'">
        <pre style="width: fit-content; max-width: 40vw;">{{ item.text }}</pre>
      </a-typography-paragraph>
      <p class="bubble-file" :style="fileJustify" v-else-if="item.type === 'FILE'">
        <a-card style="width: fit-content;background-color: rgba(237,237,237,0.88);">
          <a-card-meta :title="item.originalFileName"/>
          <template #actions>
            <span v-if="isReversal['mirror-reversal']">文件</span>
            <span @click="downloadFile(item.id)">
              <download-outlined/>
            </span>
            <span v-if="!isReversal['mirror-reversal']">文件</span>
          </template>
        </a-card>
      </p>
    </template>
    <template #datetime>
      <span>{{ item.date }}</span>
    </template>
  </a-comment>
</template>

